
def count_floor(s):
    floor = 0
    for c in s:
        if c == '(':
            floor += 1
        elif c == ')':
            floor -= 1
    return floor 


def find_first_floor(s, floor_to_find):
    floor = 0
    for i, c in enumerate(s):
        if c == '(':
            floor += 1
        elif c == ')':
            floor -= 1
        if floor == floor_to_find:
            return i + 1

